import java.util.ArrayList;import java.util.List;import java.util.Scanner;public class Main {	public static void main(String[] args) {		Scanner in = new Scanner(System.in);		int T = in.nextInt();		while (T-- > 0) {			int n = in.nextInt();			List<Integer> list = new ArrayList<Integer>();			for (int i = 0; i < n; i++) {				list.add(in.nextInt());			}			int sum = list.stream().mapToInt((x) -> x).sum();			if (sum == 2 * (n - 1)) {				System.out.println("Yes");			} else {				System.out.println("No");			}		}	}}